import React, { useState } from "react";
import "./App.css";

// Sample Course Data
const courses = [
  {
    id: 1,
    title: "React for Beginners",
    description: "Learn React from scratch and build dynamic web applications.",
    link: "https://reactjs.org/",
  },
  {
    id: 2,
    title: "JavaScript Fundamentals",
    description: "Understand JavaScript basics and write better code.",
    link: "https://developer.mozilla.org/en-US/docs/Web/JavaScript",
  },
  {
    id: 3,
    title: "Node.js for Backend",
    description: "Build server-side apps with Node.js.",
    link: "https://nodejs.org/",
  },
  {
    id: 4,
    title: "Python for Data Science",
    description: "Learn how to use Python for data analysis and visualization.",
    link: "https://www.python.org/",
  },
  {
    id: 5,
    title: "HTML & CSS Essentials",
    description: "Learn the building blocks of the web with HTML and CSS.",
    link: "https://developer.mozilla.org/en-US/docs/Learn/Getting_started_with_the_web",
  },
  {
    id: 6,
    title: "Angular Mastery",
    description: "Learn how to build dynamic apps with Angular.",
    link: "https://angular.io/",
  },
  {
    id: 7,
    title: "Machine Learning Basics",
    description: "Introduction to Machine Learning concepts and practices.",
    link: "https://www.tensorflow.org/",
  },
  {
    id: 8,
    title: "Introduction to UI/UX Design",
    description: "Learn the principles of creating effective user experiences.",
    link: "https://www.coursera.org/",
  },
  {
    id: 9,
    title: "Cybersecurity Fundamentals",
    description: "Understand the core principles of cybersecurity.",
    link: "https://www.coursera.org/",
  },
  {
    id: 10,
    title: "AWS Cloud Fundamentals",
    description: "Learn about cloud computing and AWS fundamentals.",
    link: "https://aws.amazon.com/",
  },
  {
    id: 11,
    title: "Java Fundamentals",
    description: "Learn programming with Java and object-oriented design principles.",
    link: "https://www.java.com/",
  },
  {
    id: 12,
    title: "Data Visualization with D3.js",
    description: "Learn how to create interactive visualizations with D3.js.",
    link: "https://d3js.org/",
  },
];

function App() {
  const [theme, setTheme] = useState("light");
  const [search, setSearch] = useState("");

  // Toggle Theme Function
  const toggleTheme = () => {
    setTheme(theme === "light" ? "dark" : "light");
  };

  // Handle Search Logic
  const handleSearch = (e) => {
    setSearch(e.target.value);
  };

  // Filter courses based on search input
  const filteredCourses = courses.filter((course) =>
    course.title.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className={theme === "dark" ? "dark-mode" : "light-mode"}>
      {/* Navigation Bar */}
      <nav>
        <h1 className="nav-logo">My Course App</h1>
        <button className="theme-toggle" onClick={toggleTheme}>
          {theme === "light" ? "Dark Mode" : "Light Mode"}
        </button>
      </nav>

      {/* Main Section */}
      <div className="main-content">
        <h1 className="welcome-section">Welcome to Our Course Recommendations</h1>

        {/* Search Bar */}
        <div className="search-container">
          <input
            type="text"
            className="search-bar"
            value={search}
            onChange={handleSearch}
            placeholder="Search for courses..."
          />
        </div>
      </div>

      {/* Courses Section */}
      <div className="course-section">
        <h2 className="section-header">Recommended Courses</h2>
        <div className="course-list">
          {filteredCourses.length > 0 ? (
            filteredCourses.map((course) => (
              <div className="course-card" key={course.id}>
                <h3>
                  <button
                    className="course-link"
                    onClick={() => window.open(course.link, "_blank")}
                  >
                    {course.title}
                  </button>
                </h3>
                <p>{course.description}</p>
              </div>
            ))
          ) : (
            <p>No courses match your search.</p>
          )}
        </div>
      </div>

      {/* Footer Section */}
      <footer>
        <p>Contact us at support@mycourseapp.com</p>
      </footer>
    </div>
  );
}

export default App;
