import React, { useState, useEffect, useCallback } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import CoursePage from "./CoursePage";
import "./App.css";

const App = () => {
  const [theme, setTheme] = useState(() => localStorage.getItem("theme") || "light");
  const [searchTerm, setSearchTerm] = useState("");
  const [visibleCourses, setVisibleCourses] = useState(10); // Increased initial number of visible courses

  const toggleTheme = useCallback(() => {
    const newTheme = theme === "light" ? "dark" : "light";
    setTheme(newTheme);
    localStorage.setItem("theme", newTheme);

    document.body.classList.remove("light-theme", "dark-theme");
    document.body.classList.add(`${newTheme}-theme`);
  }, [theme]);

  useEffect(() => {
    document.body.classList.add(`${theme}-theme`);
  }, [theme]);

  const courses = [
    { id: "1", title: "React for Beginners", description: "Learn React from scratch.", url: "https://www.udemy.com/course/react-for-beginners/" },
    { id: "2", title: "JavaScript Fundamentals", description: "Understand JavaScript basics.", url: "https://www.udemy.com/course/javascript-fundamentals/" },
    { id: "3", title: "HTML & CSS Mastery", description: "Responsive design with HTML and CSS.", url: "https://www.udemy.com/course/html-css-mastery/" },
    { id: "4", title: "Advanced React", description: "Advanced concepts in React development.", url: "https://www.udemy.com/course/advanced-react/" },
    { id: "5", title: "Node.js for Backend Development", description: "Backend development with Node.js.", url: "https://www.udemy.com/course/nodejs-backend/" },
    { id: "6", title: "Python for Data Science", description: "Learn Python for machine learning.", url: "https://www.udemy.com/course/python-data-science/" },
    { id: "7", title: "Vue.js Essentials", description: "Introduction to Vue.js fundamentals.", url: "https://www.udemy.com/course/vuejs-essentials/" },
    { id: "8", title: "Full-Stack Development", description: "Full-stack web development from scratch.", url: "https://www.udemy.com/course/full-stack-development/" },
    { id: "9", title: "Data Structures & Algorithms", description: "Core concepts to prepare for coding interviews.", url: "https://www.udemy.com/course/data-structures-and-algorithms/" },
    { id: "10", title: "Machine Learning A-Z", description: "Machine learning concepts using Python and R.", url: "https://www.udemy.com/course/machinelearning/" },
    { id: "11", title: "UI/UX Design Bootcamp", description: "Design intuitive user experiences.", url: "https://www.udemy.com/course/ui-ux-design-bootcamp/" },
    { id: "12", title: "Digital Marketing Masterclass", description: "SEO, PPC, and marketing strategies.", url: "https://www.udemy.com/course/digital-marketing-masterclass/" },
    { id: "13", title: "Cybersecurity Essentials", description: "Learn how to secure systems and networks.", url: "https://www.udemy.com/course/cybersecurity-essentials/" },
    { id: "14", title: "Graphic Design Masterclass", description: "Learn Photoshop & Illustrator fundamentals.", url: "https://www.udemy.com/course/graphic-design-masterclass/" },
    { id: "15", title: "Game Development with Unity", description: "Build games with the Unity game engine.", url: "https://www.udemy.com/course/game-development/" },
    { id: "16", title: "Cloud Computing Fundamentals", description: "Learn AWS, Azure, and cloud computing basics.", url: "https://www.udemy.com/course/cloud-computing/" },
    { id: "17", title: "Artificial Intelligence with Python", description: "Learn AI fundamentals with Python.", url: "https://www.udemy.com/course/ai-with-python/" },
    { id: "18", title: "AWS Certified Solutions Architect", description: "Prepare for AWS certification exams.", url: "https://www.udemy.com/course/aws-certified-solutions-architect/" },
    { id: "19", title: "Design Thinking Bootcamp", description: "Learn innovative design strategies.", url: "https://www.udemy.com/course/design-thinking-bootcamp/" },
    { id: "20", title: "Blockchain Technology", description: "Learn blockchain fundamentals and applications.", url: "https://www.udemy.com/course/blockchain-basics/" },
    // Add more courses here...
  ];

  const filteredCourses = courses.filter((course) =>
    course.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
    course.description.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const displayedCourses = filteredCourses.slice(0, visibleCourses);

  const handleLoadMore = () => {
    setVisibleCourses((prev) => prev + 10); // Increment number of visible courses by 10
  };

  return (
    <Router>
      <div className="App">
        {/* Navigation Bar */}
        <nav className="navbar">
          <h1>My Course App</h1>
          <div className="switch-container">
            <label className="switch">
              <input
                type="checkbox"
                checked={theme === "dark"}
                onChange={toggleTheme}
              />
              <span className="slider"></span>
            </label>
          </div>
        </nav>

        {/* Main Content Section */}
        <div className="content">
          <Routes>
            {/* Home Page */}
            <Route
              path="/"
              element={
                <>
                  <h1>Explore Course Recommendations</h1>
                  {/* Search Bar */}
                  <input
                    type="text"
                    placeholder="Search courses..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="search-bar"
                  />

                  {/* Course List */}
                  <section className="course-list">
                    {displayedCourses.map((course) => (
                      <Link to={`/course/${course.id}`} key={course.id} className="course-link">
                        <div className="course-card">
                          <img
                            className="course-image"
                            src={`https://via.placeholder.com/250x150?text=${encodeURIComponent(course.title)}`}
                            alt={course.title}
                          />
                          <h3>{course.title}</h3>
                          <p>{course.description}</p>
                        </div>
                      </Link>
                    ))}
                    {displayedCourses.length < filteredCourses.length && (
                      <button className="load-more" onClick={handleLoadMore}>
                        Load More
                      </button>
                    )}
                  </section>
                </>
              }
            />

            {/* Dynamic Route for Each Course */}
            <Route path="/course/:courseId" element={<CoursePage courses={courses} />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
};

export default App;
