import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useParams } from 'react-router-dom';
import './App.css';

const App = () => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch courses from the backend
  useEffect(() => {
    fetch('http://localhost:5000/api/courses') // Your backend endpoint
      .then(response => response.json())
      .then(data => {
        setCourses(data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching courses:', error);
        setLoading(false);
      });
  }, []);

  return (
    <Router>
      <div className="App">
        {/* Navigation Bar */}
        <nav>
          <ul>
            <li>
              <Link to="/">Home</Link>
            </li>
          </ul>
        </nav>

        {/* Define Routes */}
        <Routes>
          {/* Home Page */}
          <Route
            path="/"
            element={
              <div>
                <h1>Course Recommendations</h1>
                <div className="course-list">
                  {loading ? (
                    <p>Loading courses...</p>
                  ) : (
                    courses.map(course => (
                      <Link to={`/course/${course.id}`} key={course.id} className="course-block">
                        <div className="course-card">
                          <h2>{course.title}</h2>
                          <p>{course.description.slice(0, 50)}...</p> {/* Truncate description */}
                        </div>
                      </Link>
                    ))
                  )}
                </div>
              </div>
            }
          />

          {/* Course Detail Page */}
          <Route
            path="/course/:id"
            element={<CourseDetail courses={courses} loading={loading} />}
          />
        </Routes>
      </div>
    </Router>
  );
};

// Course Detail Component
const CourseDetail = ({ courses, loading }) => {
  const { id } = useParams(); // Get course ID from the URL
  const course = courses.find(course => course.id === parseInt(id));

  if (loading) {
    return <p>Loading course details...</p>;
  }

  if (!course) {
    return <p>Course not found!</p>;
  }

  return (
    <div className="course-detail">
      <h1>{course.title}</h1>
      <p>{course.description}</p>
      <Link to="/">
        <button>Back to Home</button>
      </Link>
    </div>
  );
};

export default App;
