import React, { useState, useEffect, useCallback } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import CoursePage from "./CoursePage";
import "./App.css";

const App = () => {
  const [theme, setTheme] = useState(() => localStorage.getItem("theme") || "light");
  const [searchTerm, setSearchTerm] = useState("");
  const [visibleCourses, setVisibleCourses] = useState(10);
  const [allCourses, setAllCourses] = useState([]);
  const [loading, setLoading] = useState(true);

  const toggleTheme = useCallback(() => {
    const newTheme = theme === "light" ? "dark" : "light";
    setTheme(newTheme);
    localStorage.setItem("theme", newTheme);

    document.body.classList.remove("light-theme", "dark-theme");
    document.body.classList.add(`${newTheme}-theme`);
  }, [theme]);

  useEffect(() => {
    document.body.classList.add(`${theme}-theme`);
  }, [theme]);

  // Fetch courses when the page loads
  const fetchCourses = async (url) => {
    setLoading(true);
    try {
      const response = await fetch(url);
      const data = await response.json();
      setAllCourses(data);
    } catch (error) {
      console.error("Error fetching courses:", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchCourses("http://localhost:5000/api/courses");
  }, []);

  useEffect(() => {
    if (searchTerm.trim() === "") {
      fetchCourses("http://localhost:5000/api/courses");
      return;
    }

    fetchCourses(`http://localhost:5000/api/courses/search?query=${encodeURIComponent(searchTerm)}`);
  }, [searchTerm]);

  const filteredCourses = allCourses.slice(0, visibleCourses);

  const handleLoadMore = () => {
    setVisibleCourses((prev) => prev + 10);
  };

  return (
    <Router>
      <div className="App">
        {/* Navigation Bar */}
        <nav className="navbar">
          <h1>My Course App</h1>
          {/* Animated Toggle */}
          <div className="switch-container">
            <label className="switch">
              <input
                type="checkbox"
                checked={theme === "dark"}
                onChange={toggleTheme}
              />
              <span className="slider"></span>
            </label>
          </div>
        </nav>

        {/* Main Content Section */}
        <div className="content">
          <Routes>
            {/* Home Page */}
            <Route
              path="/"
              element={
                <>
                  <h1>Explore Course Recommendations</h1>

                  {/* Search Bar */}
                  <input
                    type="text"
                    placeholder="Search courses..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="search-bar"
                  />

                  {/* Handle Loading */}
                  {loading && <p>Loading courses...</p>}

                  {/* Course List Section */}
                  <section className="course-list">
                    {filteredCourses.map((course) => (
                      <Link to={`/course/${course.id}`} key={course.id} className="course-link">
                        <div className="course-card">
                          <img
                            className="course-image"
                            src={`https://via.placeholder.com/250x150?text=${encodeURIComponent(course.title)}`}
                            alt={course.title}
                          />
                          <h3>{course.title}</h3>
                          <p>{course.description}</p>
                        </div>
                      </Link>
                    ))}
                    {filteredCourses.length < allCourses.length && (
                      <button className="load-more" onClick={handleLoadMore}>
                        Load More
                      </button>
                    )}
                  </section>
                </>
              }
            />

            {/* Course Details Page */}
            <Route path="/course/:courseId" element={<CoursePage courses={allCourses} />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
};

export default App;
