import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import "./App.css";

const App = () => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searchQuery, setSearchQuery] = useState(""); // State for search query
  const [isDarkMode, setIsDarkMode] = useState(false); // State for theme toggle

  // Fetch courses based on the search query
  useEffect(() => {
    if (searchQuery) {
      setLoading(true);
      const fetchCourses = async () => {
        try {
          const response = await fetch(
            `https://udemy-paid-courses-for-free-api.p.rapidapi.com/rapidapi/courses/search?page=1&page_size=10&query=${searchQuery}`,
            {
              method: "GET",
              headers: {
                "X-RapidAPI-Key": "3164ce217bmshf612b3bde308adcp1e4054jsnf0521bf16b74",
                "X-RapidAPI-Host": "udemy-paid-courses-for-free-api.p.rapidapi.com",
              },
            }
          );
          const data = await response.json();
          setCourses(data.results || []);
          setLoading(false);
        } catch (error) {
          console.error("Error fetching courses:", error);
          setLoading(false);
        }
      };
      fetchCourses();
    }
  }, [searchQuery]);

  // Handle search input change
  const handleSearchChange = (e) => setSearchQuery(e.target.value);

  // Handle theme toggle
  const toggleTheme = () => setIsDarkMode((prevMode) => !prevMode);

  return (
    <Router>
      <div className={`App ${isDarkMode ? "dark-theme" : "light-theme"}`}>
        {/* Navigation Bar */}
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark fixed-top">
          <div className="container">
            <Link className="navbar-brand" to="/">
              MyApp
            </Link>
            <button
              className="navbar-toggler"
              type="button"
              data-bs-toggle="collapse"
              data-bs-target="#navbarNav"
              aria-controls="navbarNav"
              aria-expanded="false"
              aria-label="Toggle navigation"
            >
              <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarNav">
              <ul className="navbar-nav ms-auto">
                <li className="nav-item">
                  <Link className="nav-link btn" to="/">
                    Home
                  </Link>
                </li>
                <li className="nav-item">
                  <Link className="nav-link btn" to="/courses">
                    Courses
                  </Link>
                </li>
                <li className="nav-item">
                  <Link className="nav-link btn" to="/contact">
                    Contact
                  </Link>
                </li>
                <li className="nav-item">
                  <Link className="nav-link btn" to="/account">
                    Account
                  </Link>
                </li>
              </ul>
              <button className="btn btn-theme-toggle" onClick={toggleTheme}>
                {isDarkMode ? "Light Theme" : "Dark Theme"}
              </button>
            </div>
          </div>
        </nav>

        {/* Routes */}
        <Routes>
          <Route
            path="/"
            element={<Home handleSearchChange={handleSearchChange} />}
          />
          <Route
            path="/courses"
            element={<Courses courses={courses} loading={loading} />}
          />
          <Route path="/contact" element={<Contact />} />
          <Route path="/account" element={<Account />} />
        </Routes>
      </div>
    </Router>
  );
};

// Home Component with Search Bar
const Home = ({ handleSearchChange }) => (
  <div className="container mt-5">
    <h1>Welcome to Our Course Recommendation</h1>
    <p>Explore and find the best courses available.</p>

    {/* Search Bar */}
    <div className="mb-4">
      <input
        type="text"
        className="form-control"
        placeholder="Search for courses (e.g., Python)"
        onChange={handleSearchChange}
        onKeyDown={(e) => e.key === "Enter" && handleSearchChange(e)}
      />
    </div>
  </div>
);

// Courses Component to Display Courses
const Courses = ({ courses, loading }) => (
  <div className="container mt-5">
    <h1>Recommended Courses</h1>
    {loading ? (
      <p>Loading courses...</p>
    ) : courses.length > 0 ? (
      <div className="course-list">
        {courses.map((course) => (
          <div key={course.id} className="course-card">
            <img
              src={course.image_240x135}
              alt={course.title}
              className="course-image"
            />
            <h2>{course.title}</h2>
            <p>{course.description || "No description available."}</p>
            <a
              href={course.url}
              target="_blank"
              rel="noopener noreferrer"
              className="btn btn-primary"
            >
              View Course
            </a>
          </div>
        ))}
      </div>
    ) : (
      <p>No courses found. Try searching for something else.</p>
    )}
  </div>
);

// Contact Component
const Contact = () => (
  <div className="container mt-5">
    <h1>Contact Us</h1>
    <p>Get in touch with us for any queries or support.</p>
  </div>
);

// Account Component
const Account = () => (
  <div className="container mt-5">
    <h1>My Account</h1>
    <p>Manage your account details and preferences.</p>
  </div>
);

export default App;
