import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import CoursePage from "./CoursePage";
import "./App.css";

const App = () => {
  const [theme, setTheme] = useState(() => localStorage.getItem("theme") || "light");

  const toggleTheme = () => {
    const newTheme = theme === "light" ? "dark" : "light";
    setTheme(newTheme);
    localStorage.setItem("theme", newTheme);

    document.body.classList.remove("light-theme", "dark-theme");
    document.body.classList.add(`${newTheme}-theme`);
  };

  useEffect(() => {
    document.body.classList.add(theme + "-theme");
  }, [theme]);

  return (
    <Router>
      <div className="App">
        {/* Navigation Bar */}
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
          <div className="container-fluid">
            <h1 className="text-white">My Course App</h1>
            <div className="form-check form-switch">
              <input
                className="form-check-input"
                type="checkbox"
                role="switch"
                checked={theme === "dark"}
                onChange={toggleTheme}
              />
              <label className="form-check-label text-white">Dark Mode</label>
            </div>
          </div>
        </nav>

        {/* Main Section */}
        <div className="container mt-3">
          <Routes>
            <Route
              path="/"
              element={
                <>
                  <h1 className="text-center">Explore Course Recommendations</h1>
                  <input
                    type="text"
                    className="form-control mt-3"
                    placeholder="Search courses..."
                  />
                  <section className="course-list mt-4">
                    <div className="course-card bg-light p-3 m-2 text-dark rounded shadow-sm">
                      Example Course 1
                    </div>
                  </section>
                </>
              }
            />

            <Route path="/course/:courseId" element={<CoursePage />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
};

export default App;
