import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";

const App = () => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch courses from the backend
  useEffect(() => {
    fetch("http://localhost:5000/api/courses") // Your backend endpoint
      .then((response) => response.json())
      .then((data) => {
        setCourses(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching courses:", error);
        setLoading(false);
      });
  }, []);

  return (
    <Router>
      <div className="App">
        {/* Navigation Bar */}
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
          <div className="container-fluid">
            <Link className="navbar-brand" to="/">
              CourseApp
            </Link>
            <button
              className="navbar-toggler"
              type="button"
              data-bs-toggle="collapse"
              data-bs-target="#navbarNav"
              aria-controls="navbarNav"
              aria-expanded="false"
              aria-label="Toggle navigation"
            >
              <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarNav">
              <ul className="navbar-nav ms-auto">
                <li className="nav-item">
                  <Link className="nav-link" to="/">
                    Home
                  </Link>
                </li>
                <li className="nav-item">
                  <Link className="nav-link" to="/recommendation">
                    Recommendations
                  </Link>
                </li>
                <li className="nav-item">
                  <a className="nav-link" href="#login">
                    Login
                  </a>
                </li>
                <li className="nav-item">
                  <a className="nav-link" href="#subscription">
                    Subscription
                  </a>
                </li>
                <li className="nav-item">
                  <a className="nav-link" href="#account">
                    Account
                  </a>
                </li>
                <li className="nav-item">
                  <a className="nav-link" href="#contact">
                    Contact
                  </a>
                </li>
              </ul>
            </div>
          </div>
        </nav>

        {/* Define Routes */}
        <Routes>
          {/* Home Page */}
          <Route
            path="/"
            element={
              <div>
                <h1>Course Recommendations</h1>
                <div className="course-list">
                  {loading ? (
                    <p>Loading courses...</p>
                  ) : (
                    courses.map((course) => (
                      <Link
                        to={`/course/${course.id}`}
                        key={course.id}
                        className="course-card"
                      >
                        <div>
                          <img
                            src={course.imageUrl}
                            alt={course.title}
                            className="course-image"
                          />
                          <h2>{course.title}</h2>
                          <p>{course.description.slice(0, 50)}...</p> {/* Truncate description */}
                        </div>
                      </Link>
                    ))
                  )}
                </div>
              </div>
            }
          />

          {/* Course Detail Page */}
          <Route
            path="/course/:id"
            element={<CourseDetail courses={courses} loading={loading} />}
          />
        </Routes>
      </div>
    </Router>
  );
};

// Course Detail Component
const CourseDetail = ({ courses, loading }) => {
  const { id } = useParams();
  const course = courses.find((course) => course.id === parseInt(id));

  if (loading) {
    return <p>Loading course details...</p>;
  }

  if (!course) {
    return <p>Course not found!</p>;
  }

  return (
    <div className="course-detail">
      <h1>{course.title}</h1>
      <p>{course.description}</p>
      <div>
        <h3>Video Content</h3>
        <video width="600" controls>
          <source src={course.videoLink} type="video/mp4" />
          Your browser does not support the video tag.
        </video>
      </div>
      <Link to="/">
        <button className="btn btn-primary">Back to Home</button>
      </Link>
    </div>
  );
};

export default App;
