import React, { useState } from "react";
import "./App.css";

// Sample Course Data
const courses = [
  { id: 1, title: "React for Beginners", description: "Learn React from scratch and build dynamic web applications.", link: "https://reactjs.org/" },
  { id: 2, title: "JavaScript Fundamentals", description: "Understand JavaScript basics and write better code.", link: "https://developer.mozilla.org/en-US/docs/Web/JavaScript" },
  // Add more courses here
];

function App() {
  const [theme, setTheme] = useState("light");
  const [search, setSearch] = useState("");

  // Theme toggle function
  const toggleTheme = () => {
    setTheme((prevTheme) => (prevTheme === "light" ? "dark" : "light"));
  };

  const handleSearch = (e) => {
    setSearch(e.target.value);
  };

  const filteredCourses = courses.filter((course) =>
    course.title.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className={theme === "dark" ? "dark-mode" : "light-mode"}>
      {/* Navigation Bar */}
      <nav>
        <h1 className="nav-logo">My Course App</h1>
        <button className="theme-toggle" onClick={toggleTheme}>
          Toggle Theme
        </button>
      </nav>

      {/* Main Section */}
      <div className="main-content">
        <h1 className="welcome-section">Welcome to Our Course Recommendations</h1>

        {/* Search Bar */}
        <div className="search-container">
          <input
            type="text"
            className="search-bar"
            value={search}
            onChange={handleSearch}
            placeholder="Search for courses..."
          />
        </div>
      </div>

      {/* Course List Section */}
      <div className="course-section">
        <h2 className="section-header">Recommended Courses</h2>
        <div className="course-list">
          {filteredCourses.map((course) => (
            <div className="course-card" key={course.id}>
              <h3>
                <a
                  className="course-link"
                  href={course.link}
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  {course.title}
                </a>
              </h3>
              <p>{course.description}</p>
            </div>
          ))}
        </div>
      </div>

      {/* Footer Section */}
      <footer>
        <p>&copy; 2024 My Course App. All rights reserved.</p>
        <div className="social-icons">
          <a href="https://facebook.com" target="_blank" rel="noopener noreferrer">
            <i className="fab fa-facebook-f"></i>
          </a>
          <a href="https://twitter.com" target="_blank" rel="noopener noreferrer">
            <i className="fab fa-twitter"></i>
          </a>
          <a href="https://instagram.com" target="_blank" rel="noopener noreferrer">
            <i className="fab fa-instagram"></i>
          </a>
          <a href="https://github.com" target="_blank" rel="noopener noreferrer">
            <i className="fab fa-github"></i>
          </a>
          <a href="https://youtube.com" target="_blank" rel="noopener noreferrer">
            <i className="fab fa-youtube"></i>
          </a>
        </div>
      </footer>
    </div>
  );
}

export default App;
