import React, { useState, useEffect, useCallback } from "react";
import "./App.css";

const App = () => {
  const [theme, setTheme] = useState("light");

  // Handle toggling themes with useCallback
  const toggleTheme = useCallback(() => {
    const newTheme = theme === "light" ? "dark" : "light";
    setTheme(newTheme);

    document.body.classList.remove("light-theme", "dark-theme");
    document.body.classList.add(`${newTheme}-theme`);
  }, [theme]);

  useEffect(() => {
    document.body.classList.add(`${theme}-theme`);
  }, [theme]);

  const courses = [
    { title: "React for Beginners", description: "Learn React from scratch.", url: "https://www.udemy.com/course/react-for-beginners/" },
    { title: "JavaScript Fundamentals", description: "Understand JavaScript basics.", url: "https://www.udemy.com/course/javascript-fundamentals/" },
    { title: "HTML & CSS Mastery", description: "Responsive design with HTML and CSS.", url: "https://www.udemy.com/course/html-css-mastery/" },
    { title: "Advanced React", description: "Advanced concepts in React development.", url: "https://www.udemy.com/course/advanced-react/" },
    { title: "Node.js for Backend Development", description: "Backend development with Node.js.", url: "https://www.udemy.com/course/nodejs-backend/" },
    { title: "Python for Data Science", description: "Learn Python for machine learning.", url: "https://www.udemy.com/course/python-data-science/" },
    { title: "Vue.js Essentials", description: "Introduction to Vue.js fundamentals.", url: "https://www.udemy.com/course/vuejs-essentials/" },
    { title: "Full-Stack Development", description: "Full-stack web development from scratch.", url: "https://www.udemy.com/course/full-stack-development/" },
    { title: "Data Structures & Algorithms", description: "Core concepts to prepare for coding interviews.", url: "https://www.udemy.com/course/data-structures-and-algorithms/" },
    { title: "Machine Learning A-Z", description: "Machine learning concepts using Python and R.", url: "https://www.udemy.com/course/machinelearning/" },
    { title: "UI/UX Design Bootcamp", description: "Design intuitive user experiences.", url: "https://www.udemy.com/course/ui-ux-design-bootcamp/" },
    { title: "Digital Marketing Masterclass", description: "SEO, PPC, and marketing strategies.", url: "https://www.udemy.com/course/digital-marketing-masterclass/" },
    { title: "Cybersecurity Essentials", description: "Learn how to secure systems and networks.", url: "https://www.udemy.com/course/cybersecurity-essentials/" },
    { title: "Graphic Design Masterclass", description: "Learn Photoshop & Illustrator fundamentals.", url: "https://www.udemy.com/course/graphic-design-masterclass/" },
    { title: "Game Development with Unity", description: "Build games with the Unity game engine.", url: "https://www.udemy.com/course/game-development/" },
    { title: "Cloud Computing Fundamentals", description: "Learn AWS, Azure, and cloud computing basics.", url: "https://www.udemy.com/course/cloud-computing/" },
    { title: "Artificial Intelligence with Python", description: "Learn AI fundamentals with Python.", url: "https://www.udemy.com/course/ai-with-python/" },
    { title: "AWS Certified Solutions Architect", description: "Prepare for AWS certification exams.", url: "https://www.udemy.com/course/aws-certified-solutions-architect/" },
    { title: "Design Thinking Bootcamp", description: "Learn innovative design strategies.", url: "https://www.udemy.com/course/design-thinking-bootcamp/" },
    { title: "Blockchain Technology", description: "Learn blockchain fundamentals and applications.", url: "https://www.udemy.com/course/blockchain-basics/" }
  ];

  return (
    <div className="App">
      {/* Navigation Bar */}
      <nav className="navbar">
        <h1>My Course App</h1>
        <div className="switch-container">
          {/* Toggle Switch */}
          <label className="switch">
            <input
              type="checkbox"
              checked={theme === "dark"}
              onChange={toggleTheme}
            />
            <span className="slider"></span>
          </label>
        </div>
      </nav>

      {/* Main Content Section */}
      <div className="content">
        <h1>Explore Course Recommendations</h1>
        <section className="course-list">
          {courses.map((course, index) => (
            <div key={index} className="course-card">
              <img className="course-image"
                src={`https://via.placeholder.com/250x150?text=${encodeURIComponent(course.title)}`}
                alt={course.title}
              />
              <h3>
                <a href={course.url} target="_blank" rel="noopener noreferrer">
                  {course.title}
                </a>
              </h3>
              <p>{course.description}</p>
            </div>
          ))}
        </section>
      </div>
    </div>
  );
};

export default App;
