import React, { useState } from "react";
import "./App.css";

// Sample Course Data
const courses = [
  {
    id: 1,
    title: "React for Beginners",
    description: "Learn React from scratch and build dynamic web applications.",
    link: "https://reactjs.org/",
  },
  {
    id: 2,
    title: "JavaScript Fundamentals",
    description: "Understand JavaScript basics and write better code.",
    link: "https://developer.mozilla.org/en-US/docs/Web/JavaScript",
  },
  {
    id: 3,
    title: "Node.js for Backend",
    description: "Build server-side apps with Node.js.",
    link: "https://nodejs.org/",
  },
  {
    id: 4,
    title: "HTML & CSS Mastery",
    description: "Create responsive and modern web designs.",
    link: "https://developer.mozilla.org/en-US/docs/Learn/Getting_started_with_the_web/HTML_basics",
  },
  {
    id: 5,
    title: "Python for Data Science",
    description: "Analyze data and build ML models using Python.",
    link: "https://www.python.org/",
  },
  {
    id: 6,
    title: "UI/UX Design Bootcamp",
    description: "Design intuitive and user-friendly interfaces.",
    link: "https://www.adobe.com/",
  },
  {
    id: 7,
    title: "Cybersecurity Basics",
    description: "Protect systems and networks from cyber threats.",
    link: "https://www.cybersecurity.org/",
  },
  {
    id: 8,
    title: "AWS Cloud Fundamentals",
    description: "Learn cloud computing concepts with AWS.",
    link: "https://aws.amazon.com/",
  },
];

function App() {
  const [theme, setTheme] = useState("light");
  const [search, setSearch] = useState("");

  const toggleTheme = () => {
    setTheme(theme === "light" ? "dark" : "light");
  };

  const handleSearch = (e) => {
    setSearch(e.target.value);
  };

  const filteredCourses = courses.filter((course) =>
    course.title.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className={theme === "dark" ? "dark-mode" : "light-mode"}>
      {/* Navigation Bar */}
      <nav>
        <a href="#" className="nav-logo">
          My Course App
        </a>
        <button className="theme-toggle" onClick={toggleTheme}>
          Toggle Theme
        </button>
      </nav>

      {/* Main Section */}
      <div className="main-content">
        <h1 className="welcome-section">Welcome to Our Course Recommendations</h1>

        {/* Search Bar */}
        <div className="search-container">
          <input
            type="text"
            className="search-bar"
            value={search}
            onChange={handleSearch}
            placeholder="Search for courses..."
          />
        </div>
      </div>

      {/* Course List Section */}
      <div className="course-section">
        <h2 className="section-header">Recommended Courses</h2>
        <div className="course-list">
          {filteredCourses.map((course) => (
            <div className="course-card" key={course.id}>
              <h3>
                <a href={course.link} target="_blank" rel="noopener noreferrer">
                  {course.title}
                </a>
              </h3>
              <p>{course.description}</p>
            </div>
          ))}
        </div>
      </div>

      {/* Footer Section */}
      <footer>
        <p>Contact us at support@mycourseapp.com</p>
      </footer>
    </div>
  );
}

export default App;
