import React from "react";
import ReactDOM from "react-dom/client";
import App from "./App";
import "bootstrap/dist/css/bootstrap.min.css"; // Import Bootstrap CSS
import "bootstrap/dist/js/bootstrap.bundle.min.js"; // Import Bootstrap JS
import "./App.css"; // Import your custom CSS file

// Use createRoot for React 18+
const root = ReactDOM.createRoot(document.getElementById("root"));

root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);
// Function to open the modal with embedded content
function openModal(url) {
  const modalOverlay = document.getElementById('modalOverlay');
  const modalIframe = document.getElementById('modalIframe');

  modalIframe.src = url; // Dynamically load content into iframe
  modalOverlay.style.display = "flex";
}

// Close the modal
function closeModal() {
  const modalOverlay = document.getElementById('modalOverlay');
  const modalIframe = document.getElementById('modalIframe');

  modalIframe.src = ''; // Clear iframe content
  modalOverlay.style.display = "none";
}

// Attach click handlers dynamically
document.querySelectorAll('.btn').forEach(button => {
  button.addEventListener('click', (event) => {
    const link = event.target.closest('a').href;

    if (link.includes('youtube.com')) {
      const videoId = link.split('v=')[1].split('&')[0]; // Extract YouTube video ID
      const embedUrl = `https://www.youtube.com/embed/${videoId}`;
      openModal(embedUrl);
    } else if (link.includes('tinyurl.com')) {
      const embedUrl = link;
      openModal(embedUrl);
    }
  });
});
