import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";

// Main App Component
const App = () => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");

  // Fetch courses from API
  const fetchCourses = (query) => {
    setLoading(true);
    const udemyUrl = `https://udemy-paid-courses-for-free-api.p.rapidapi.com/rapidapi/courses/search?page=1&page_size=10&query=${query}`;
    const apiKey = "3164ce217bmshf612b3bde308adcp1e4054jsnf0521bf16b74"; // Replace with your API key

    fetch(udemyUrl, {
      method: "GET",
      headers: {
        "Authorization": `Bearer ${apiKey}`,
        "Content-Type": "application/json",
      },
    })
      .then((response) => response.json())
      .then((data) => {
        setCourses(data.results);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching courses:", error);
        setLoading(false);
      });
  };

  // Handle search bar updates
  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleSearchKeyPress = (e) => {
    if (e.key === "Enter") {
      fetchCourses(searchQuery);
    }
  };

  return (
    <Router>
      <div className="App">
        {/* Navigation Bar */}
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark fixed-top">
          <div className="container">
            <Link className="navbar-brand" to="/">
              MyApp
            </Link>
            <button
              className="navbar-toggler"
              type="button"
              data-bs-toggle="collapse"
              data-bs-target="#navbarNav"
              aria-controls="navbarNav"
              aria-expanded="false"
              aria-label="Toggle navigation"
            >
              <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarNav">
              <ul className="navbar-nav ms-auto">
                <li className="nav-item">
                  <Link className="nav-link btn btn-link" to="/">
                    Home
                  </Link>
                </li>
                <li className="nav-item">
                  <Link className="nav-link btn btn-link" to="/courses">
                    Courses
                  </Link>
                </li>
                <li className="nav-item">
                  <Link className="nav-link btn btn-link" to="/contact">
                    Contact
                  </Link>
                </li>
                <li className="nav-item">
                  <Link className="nav-link btn btn-link" to="/account">
                    Account
                  </Link>
                </li>
              </ul>
            </div>
          </div>
        </nav>

        {/* Routes */}
        <Routes>
          <Route
            path="/"
            element={
              <Home
                handleSearchChange={handleSearchChange}
                handleSearchKeyPress={handleSearchKeyPress}
              />
            }
          />
          <Route
            path="/courses"
            element={<Courses courses={courses} loading={loading} />}
          />
          <Route path="/contact" element={<Contact />} />
          <Route path="/account" element={<Account />} />
        </Routes>
      </div>
    </Router>
  );
};

// Home Component
const Home = ({ handleSearchChange, handleSearchKeyPress }) => {
  return (
    <div className="container mt-5">
      <h1>Welcome to Our Course Recommendation</h1>
      <p>Explore and find the best courses available.</p>

      {/* Search Bar */}
      <div className="mb-4">
        <input
          type="text"
          className="form-control"
          placeholder="Search for courses (e.g., Python)"
          onChange={handleSearchChange}
          onKeyDown={handleSearchKeyPress}
        />
      </div>
    </div>
  );
};

// Courses Component
const Courses = ({ courses, loading }) => {
  return (
    <div className="container mt-5">
      <h1>Courses</h1>
      {loading ? (
        <p>Loading courses...</p>
      ) : courses.length > 0 ? (
        courses.map((course) => (
          <div key={course.id} className="course-card mb-4">
            <h3>{course.title}</h3>
            <p>{course.description}</p>
            <img
              src={course.image_240x135}
              alt={course.title}
              className="img-fluid"
            />
            <a href={course.url} target="_blank" rel="noopener noreferrer">
              View Course
            </a>
          </div>
        ))
      ) : (
        <p>No courses found for your search.</p>
      )}
    </div>
  );
};

// Contact Component
const Contact = () => {
  return (
    <div className="container mt-5">
      <h1>Contact Us</h1>
      <p>Get in touch with us for any queries or support.</p>
    </div>
  );
};

// Account Component
const Account = () => {
  return (
    <div className="container mt-5">
      <h1>My Account</h1>
      <p>Manage your account details and preferences.</p>
    </div>
  );
};

export default App;
