import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min.js";
import "./App.css";

// Main App Component
const App = () => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");

  const fetchCourses = async (query) => {
    if (!query) return;

    try {
      setLoading(true);
      const url = `https://udemy-paid-courses-for-free-api.p.rapidapi.com/rapidapi/courses/search?page=1&page_size=10&query=${query}`;
      const response = await fetch(url, {
        method: "GET",
        headers: {
          "X-RapidAPI-Key": "3164ce217bmshf612b3bde308adcp1e4054jsnf0521bf16b74",
          "X-RapidAPI-Host": "udemy-paid-courses-for-free-api.p.rapidapi.com",
        },
      });

      const data = await response.json();
      setCourses(data.results);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching courses", error);
      setLoading(false);
    }
  };

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleSearchSubmit = (e) => {
    if (e.key === "Enter") {
      fetchCourses(searchQuery);
    }
  };

  return (
    <Router>
      <div className="App">
        {/* Navbar */}
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark fixed-top">
          <div className="container">
            <Link className="navbar-brand" to="/">
              MyApp
            </Link>
            <button
              className="navbar-toggler"
              type="button"
              data-bs-toggle="collapse"
              data-bs-target="#navbarNav"
              aria-controls="navbarNav"
              aria-expanded="false"
              aria-label="Toggle navigation"
            >
              <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarNav">
              <ul className="navbar-nav ms-auto">
                <li className="nav-item">
                  <Link className="nav-link" to="/">
                    Home
                  </Link>
                </li>
                <li className="nav-item">
                  <Link className="nav-link" to="/courses">
                    Courses
                  </Link>
                </li>
                <li className="nav-item">
                  <Link className="nav-link" to="/contact">
                    Contact
                  </Link>
                </li>
                <li className="nav-item">
                  <Link className="nav-link" to="/account">
                    Account
                  </Link>
                </li>
              </ul>
            </div>
          </div>
        </nav>

        {/* Routes */}
        <Routes>
          <Route
            path="/"
            element={
              <Home
                searchQuery={searchQuery}
                handleSearchChange={handleSearchChange}
                handleSearchSubmit={handleSearchSubmit}
              />
            }
          />
          <Route path="/courses" element={<Courses courses={courses} loading={loading} />} />
          <Route path="/contact" element={<Contact />} />
          <Route path="/account" element={<Account />} />
        </Routes>
      </div>
    </Router>
  );
};

// Home Component with Search
const Home = ({ searchQuery, handleSearchChange, handleSearchSubmit }) => {
  return (
    <div className="container mt-5">
      <h1>Welcome to Our Course Recommendation</h1>
      <p>Explore and find the best courses available.</p>

      {/* Search */}
      <div className="mb-4">
        <input
          type="text"
          className="form-control"
          placeholder="Search for courses..."
          value={searchQuery}
          onChange={handleSearchChange}
          onKeyDown={handleSearchSubmit}
        />
      </div>
    </div>
  );
};

// Courses List Component
const Courses = ({ courses, loading }) => {
  return (
    <div className="container mt-5">
      <h1>Courses</h1>
      {loading ? (
        <p>Loading courses...</p>
      ) : courses?.length > 0 ? (
        courses.map((course) => (
          <div key={course.id} className="course-card mb-4">
            <h3>{course.title}</h3>
            <p>{course.description}</p>
            <a href={course.url} target="_blank" rel="noopener noreferrer">
              View Course
            </a>
          </div>
        ))
      ) : (
        <p>No courses found for your search.</p>
      )}
    </div>
  );
};

// Contact Component
const Contact = () => {
  return (
    <div className="container mt-5">
      <h1>Contact Us</h1>
      <p>For inquiries, contact us anytime.</p>
    </div>
  );
};

// Account Component
const Account = () => {
  return (
    <div className="container mt-5">
      <h1>My Account</h1>
      <p>Manage your account preferences here.</p>
    </div>
  );
};

export default App;
