import React, { useState, useEffect, useCallback } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import CoursePage from "./CoursePage";
import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";

const App = () => {
  const [theme, setTheme] = useState(() => localStorage.getItem("theme") || "light");
  const [searchTerm, setSearchTerm] = useState("");
  const [visibleCourses, setVisibleCourses] = useState(10);
  const [allCourses, setAllCourses] = useState([]);

  const toggleTheme = useCallback(() => {
    const newTheme = theme === "light" ? "dark" : "light";
    setTheme(newTheme);
    localStorage.setItem("theme", newTheme);

    document.body.classList.remove("light-theme", "dark-theme");
    document.body.classList.add(`${newTheme}-theme`);
  }, [theme]);

  useEffect(() => {
    document.body.classList.remove("light-theme", "dark-theme");
    document.body.classList.add(`${theme}-theme`);
  }, [theme]);

  // Fetch initial courses from server
  useEffect(() => {
    fetch("http://localhost:5000/api/courses")
      .then((response) => response.json())
      .then((data) => setAllCourses(data))
      .catch((error) => console.error("Error fetching courses:", error));
  }, []);

  const filteredCourses = allCourses.slice(0, visibleCourses);

  const handleLoadMore = () => {
    setVisibleCourses((prev) => prev + 10);
  };

  return (
    <Router>
      <div className="App">
        {/* Navigation Bar with Theme Toggle */}
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
          <div className="container-fluid">
            <h1 className="navbar-brand">My Course App</h1>

            {/* Toggle Switch using Bootstrap */}
            <div className="form-check form-switch">
              <input
                className="form-check-input"
                type="checkbox"
                id="themeToggle"
                checked={theme === "dark"}
                onChange={toggleTheme}
              />
              <label className="form-check-label" htmlFor="themeToggle">
                {theme === "light" ? "Light Mode" : "Dark Mode"}
              </label>
            </div>
          </div>
        </nav>

        {/* Main Content Section */}
        <div className="content">
          <Routes>
            {/* Home Page */}
            <Route
              path="/"
              element={
                <>
                  <h1 className="text-center my-4">Explore Course Recommendations</h1>

                  {/* Search Bar */}
                  <input
                    type="text"
                    placeholder="Search courses..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="form-control mb-4"
                  />

                  {/* Course List */}
                  <section className="course-list">
                    {filteredCourses.map((course) => (
                      <div className="course-card">
                        <img
                          className="course-image"
                          src={`https://via.placeholder.com/250x150?text=${encodeURIComponent(
                            course.title
                          )}`}
                          alt={course.title}
                        />
                        <h3>{course.title}</h3>
                        <p>{course.description}</p>
                      </div>
                    ))}
                    {filteredCourses.length < allCourses.length && (
                      <button className="btn btn-primary mt-3" onClick={handleLoadMore}>
                        Load More
                      </button>
                    )}
                  </section>
                </>
              }
            />

            {/* Dynamic Route for Course Info */}
            <Route path="/course/:courseId" element={<CoursePage courses={allCourses} />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
};

export default App;
