import React, { useState, useEffect, useCallback } from "react";
import "./App.css";

const App = () => {
  const [theme, setTheme] = useState("light");

  // Use useCallback to memoize toggleTheme
  const toggleTheme = useCallback(() => {
    const newTheme = theme === "light" ? "dark" : "light";
    setTheme(newTheme);

    // Dynamically change the body's theme class
    document.body.classList.remove("light-theme", "dark-theme");
    document.body.classList.add(`${newTheme}-theme`);
  }, [theme]);

  useEffect(() => {
    // On initial app load, apply the default theme
    toggleTheme();
  }, [toggleTheme]);

  const courses = [
    {
      title: "React for Beginners",
      description: "Learn React from scratch and build dynamic web applications.",
      url: "https://www.udemy.com/course/react-for-beginners/",
    },
    {
      title: "JavaScript Fundamentals",
      description: "Understand the basics of JavaScript, a foundational programming language.",
      url: "https://www.udemy.com/course/javascript-fundamentals/",
    },
    {
      title: "HTML & CSS Mastery",
      description: "Master HTML and CSS to design beautiful and responsive websites.",
      url: "https://www.udemy.com/course/html-css-mastery/",
    },
    {
      title: "Advanced React",
      description: "Take your React skills to the next level and build complex applications.",
      url: "https://www.udemy.com/course/advanced-react/",
    },
    {
      title: "Node.js for Backend Development",
      description: "Learn how to build server-side applications with Node.js and Express.",
      url: "https://www.udemy.com/course/nodejs-backend/",
    },
    {
      title: "Python for Data Science",
      description: "Explore the powerful Python language for data science and machine learning.",
      url: "https://www.udemy.com/course/python-data-science/",
    },
    {
      title: "Vue.js Essentials",
      description: "Discover the simplicity and power of Vue.js for frontend development.",
      url: "https://www.udemy.com/course/vuejs-essentials/",
    },
    {
      title: "Full-Stack Development",
      description: "Learn how to build full-stack applications from scratch.",
      url: "https://www.udemy.com/course/full-stack-development/",
    },
    {
      title: "UI/UX Design Principles",
      description: "Learn how to design intuitive, user-friendly interfaces.",
      url: "https://www.udemy.com/course/ui-ux-design/",
    },
    {
      title: "Data Analysis with R",
      description: "Learn how to analyze data using R programming.",
      url: "https://www.udemy.com/course/data-analysis-r/",
    },
  ];

  return (
    <div className="App">
      {/* Navigation Bar */}
      <nav className="navbar">
        <h1>My Course App</h1>
        <button className="btn-theme-toggle" onClick={toggleTheme}>
          {theme === "light" ? "Switch to Dark Theme" : "Switch to Light Theme"}
        </button>
      </nav>

      {/* Main Content */}
      <div className="content">
        <h1>Welcome to Our Course Recommendations</h1>

        {/* Recommended Courses Section */}
        <section className="course-list">
          {courses.map((course, index) => (
            <div key={index} className="course-card">
              <img
                className="course-image"
                src={`https://via.placeholder.com/250x150?text=${encodeURIComponent(
                  course.title
                )}`}
                alt={course.title}
              />
              <h3>
                <a href={course.url} target="_blank" rel="noopener noreferrer">
                  {course.title}
                </a>
              </h3>
              <p>{course.description}</p>
            </div>
          ))}
        </section>
      </div>
    </div>
  );
};

export default App;
