import React, { useState, useEffect, useCallback } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import CoursePage from "./CoursePage";
import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSun, faMoon } from "@fortawesome/free-solid-svg-icons";

const App = () => {
  const [theme, setTheme] = useState(() => localStorage.getItem("theme") || "light");

  const toggleTheme = useCallback(() => {
    const newTheme = theme === "light" ? "dark" : "light";
    setTheme(newTheme);
    localStorage.setItem("theme", newTheme);

    document.body.classList.remove("light-theme", "dark-theme");
    document.body.classList.add(`${newTheme}-theme`);
  }, [theme]);

  useEffect(() => {
    document.body.classList.remove("light-theme", "dark-theme");
    document.body.classList.add(`${theme}-theme`);
  }, [theme]);

  return (
    <Router>
      <div className="App">
        {/* Navigation Bar with Theme Toggle */}
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
          <div className="container-fluid">
            <h1 className="navbar-brand">My Course App</h1>

            {/* Theme toggle with sun/moon icon */}
            <div className="form-check form-switch">
              <input
                className="form-check-input"
                type="checkbox"
                id="themeToggle"
                checked={theme === "dark"}
                onChange={toggleTheme}
              />
              <label className="form-check-label" htmlFor="themeToggle">
                <FontAwesomeIcon icon={theme === "dark" ? faMoon : faSun} />
              </label>
            </div>
          </div>
        </nav>

        {/* Main Content Section */}
        <div className="content">
          <Routes>
            {/* Home Page */}
            <Route
              path="/"
              element={
                <>
                  <h1 className="text-center my-4">Explore Course Recommendations</h1>
                  {/* Search Bar */}
                  <input
                    type="text"
                    placeholder="Search courses..."
                    className="form-control mb-4"
                  />
                </>
              }
            />
            {/* Dynamic Route for Course Info */}
            <Route path="/course/:courseId" element={<CoursePage courses={[]} />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
};

export default App;
