import React, { useState, useEffect, useCallback } from "react";
import { BrowserRouter as Router, Routes, Route, Link, useNavigate } from "react-router-dom";
import "./App.css";

const courses = [
  { id: 1, title: "React for Beginners", description: "Learn React from scratch.", url: "https://www.udemy.com/course/react-for-beginners/" },
  { id: 2, title: "JavaScript Fundamentals", description: "Understand JavaScript basics.", url: "https://www.udemy.com/course/javascript-fundamentals/" },
  { id: 3, title: "HTML & CSS Mastery", description: "Responsive design with HTML and CSS.", url: "https://www.udemy.com/course/html-css-mastery/" },
  { id: 4, title: "Advanced React", description: "Advanced concepts in React development.", url: "https://www.udemy.com/course/advanced-react/" },
  { id: 5, title: "Node.js for Backend Development", description: "Backend development with Node.js.", url: "https://www.udemy.com/course/nodejs-backend/" }
];

const App = () => {
  const [theme, setTheme] = useState(() => localStorage.getItem("theme") || "light");

  // Handle toggling themes
  const toggleTheme = useCallback(() => {
    const newTheme = theme === "light" ? "dark" : "light";
    setTheme(newTheme);
    localStorage.setItem("theme", newTheme);

    document.body.classList.remove("light-theme", "dark-theme");
    document.body.classList.add(`${newTheme}-theme`);
  }, [theme]);

  useEffect(() => {
    document.body.classList.add(`${theme}-theme`);
  }, [theme]);

  return (
    <Router>
      <div className="App">
        {/* Navigation Bar */}
        <nav className="navbar">
          <h1>My Course App</h1>
          <div className="switch-container">
            {/* Toggle Switch */}
            <label className="switch">
              <input
                type="checkbox"
                checked={theme === "dark"}
                onChange={toggleTheme}
              />
              <span className="slider"></span>
            </label>
          </div>
        </nav>

        {/* Routes */}
        <Routes>
          <Route path="/" element={<CourseList />} />
          <Route path="/course/:id" element={<CourseDetails />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
